mapper.select:
SELECT m3.id AS id, m3.name AS NAME, m3.link_url AS linkUrl, r.id AS roleId, r.name AS roleName, m3.create_time AS createTime FROM role r JOIN role_menu rm ON r.id=rm.role_id JOIN menu m3 ON rm.menu_id = m3.id JOIN menu m2 ON m3.parent_menu_id = m2.id WHERE m3.level = 3
AND m3.parent_menu_id = #{thirdMenuPageQuery.parentMenuId}
AND m3.name LIKE "%"#{thirdMenuPageQuery.name}"%"
AND m3.id = #{thirdMenuPageQuery.id}
ORDER BY m3.create_time DESC

mapper.update:
UPDATE menu


        menu.name=#{thirdMenuDTO.name},


        menu.link_url=#{thirdMenuDTO.linkUrl},


        menu.description=#{thirdMenuDTO.description}

    WHERE menu.id=#{thirdMenuDTO.id}
